import './ButtonBar.style.css';
import randomColorGenerator from '../../utils/randomColorGenerator';
import React, { useState } from 'react';

const ButtonBarComponent = ({setColorArr}) => {
    const [count, setCount] = useState(0);
    const [isDeleteButtonDisabled, setDeleteButtonDisable] = useState(true);

    const addColorToList = () => {
        const randomColor = randomColorGenerator();
        setColorArr(oldArray => [randomColor, ...oldArray]); 
        setCount(count + 1);
    }

    const deleteColorFromList = (e) => {
        console.log(count)
        if (count > 0) {
            setDeleteButtonDisable(false);
            setCount(count - 1);
            setColorArr(oldArray => oldArray.slice(0,-1));
            console.log(isDeleteButtonDisabled)
        } else {
            setDeleteButtonDisable(true);

        }
    }

    return (
        <div className="button_container">
            <button onClick={addColorToList}>+</button>
            <div>{count}</div>
            <button onClick={(e) => deleteColorFromList(e)} disabled={isDeleteButtonDisabled}>-</button>
        </div>
    )
}

// use own state here

export default ButtonBarComponent;
